const world2npcs = "Bandit_Bob Walupiggy Cactolyte Carpetiem Centurion Clown Constructor_Crow Cowbo_Jones Desert_Davey Djonnut Fishpaste97 Goldric Loominadi Obol_Altar Omar_Da_Ogar Postboy_Pablob Scubidew Snake_Jar Speccius Wellington Whattso XxX_Cattleprod_XxX Gangster_Gus";
const world3npcs = "Bellows Bill_Brr Carpenter_Cardinal Crystalswine Hoggindaz Iceland_Irwin Lonely_Hunter Lord_of_the_Hunt Shuvelle Snouts Worldo Yondergreen Worldo";
const world4npcs = "Gobo Oinkin Capital_P Blobbo";

export class NPC {
    constructor(public name: string, public data: NpcData) { }

    getClass = () => {
        return `icons-5050 icons-${this.name.replace(/ /g, "_").toLowerCase()}`
    }

    getWorld = () => {
        if (world1npcs.split(" ").includes(this.name.replace(/ /g, "_"))) {
            return "Blunder Hills";
        }
        if (world2npcs.split(" ").includes(this.name.replace(/ /g, "_"))) {
            return "Yum-Yum Desert";
        }
        if (world3npcs.split(" ").includes(this.name.replace(/ /g, "_"))) {
            return "Frostbite Tundra";
        }
        if (world4npcs.split(" ").includes(this.name.replace(/ /g, "_"))) {
            return "Hyperion Nebula";
        }

        return "Unknown";
    }
}

